mapper.select:
select fund_type_name as fundTypeName,sum(money) as fundTypeMoney from fund_pay where enabled = 1 and year_num = #{yearNum} and month_num = #{monthNum}
and sys_org_code = #{sysOrgCode}
group by fund_type_name

select month_num as monthNum,sum(money) as monthMoney from fund_pay where enabled = 1 and year_num = #{yearNum}
and sys_org_code = #{sysOrgCode}
group by month_num order by month_num

select content,money as sumMoney from fund_pay where enabled = 1 and year_num = #{yearNum} and month_num = #{monthNum}
 and sys_org_code = #{sysOrgCode}
group by money order by sumMoney desc;
